(
    TITLE,
    REG_ID,
    REG_DT,
    UPD_ID,
    UPD_DT
)
VALUES
    (
      #{title}
    , #{regId}
    , NOW()
    , #{updId}
    , NOW()
    )

mapper.update:
UPDATE ch_novel
SET TITLE = #{title}
  , UPD_ID  = #{updId}
  , UPD_DT  = NOW()
WHERE NOVEL_NO = #{novelNo}
